import React from 'react'
import ModelStore from "../../stores/ModelStore";
import * as MyActions from "../../actions/MyActions";
import $ from 'jquery';
import { dict } from '../../Dict';
import Header from "../header/header.jsx";
import DatePicker from "react-datepicker";
import queryString from 'query-string'

export default class MeetingCreate extends React.Component {

  constructor(props) {
    super(props);
    //this.submit = this.submit.bind(this);
    this.setInstance = this.setInstance.bind(this);
    this.handleChange = this.handleChange.bind(this);

    this.state = {
      token: window.localStorage.getItem('token'),
      title: null,
      info: null,
      meeting_type: null,
      start_date: Date.now(),
      end_date: null,
      event_id: null,
    }

  }



  componentWillMount() {
    ModelStore.on("set_instance", this.setInstance);

  }

  componentWillUnmount() {
    ModelStore.removeListener("set_instance", this.setInstance);
  }

  componentDidMount() {
    const value = queryString.parse(this.props.location.search);
    const event_id = value.event_id;
    this.setState({event_id})
  }


  handleChange(obj) {
    console.log(obj)
    this.setState(obj);
  }

  setInstance() {

  }

  submit() {
    var data = this.state
    MyActions.setInstance('meetings', data, this.state.token);
  }



  changeType(e){
    var self =  this;
    $('.form-selectgroup-input').each(function(){
      if ($(this).val() !== e){
        $(this).prop("checked", false )
      } else {
        if($(this).checked){
          $(this).prop("checked", false )
        } else {
          $(this).prop("checked", true )
          self.setState({meeting_type: e})
        }
      }
    })
  }


  render() {

    return (
      <body className="antialiased">
        <div className="wrapper">
          <Header />
          <div className="page-wrapper">
            <div className="container-xl">
              <div className="page-header d-print-none">
                <div className="row align-items-center"></div>
                <div className="col">
                  <div className="page-pretitle">You can create a Meeting in this page</div>
                  <h2 className="page-title">Please provide the following information.</h2>
                </div>
              </div>
            </div>
            <div className="page-body">
              <div className="container-xl">
                <div className="row row-deck row-cards">
                  <div class="col-md-8">
                    <div class="card">
                      <div class="card-header">
                        <h3 class="card-title">Create Meeting</h3>
                      </div>
                      <div class="card-body">
                        <form>
                          <div class="mb-3">
                            <label class="form-label">Meeting Title</label>
                            <input type="text" class="form-control" name="text-input" placeholder="Event's title" onInput={(e) => {this.handleChange({ title: e.target.value })}} />
                          </div>
                          <div class="mb-3">
                            <label class="form-label">Meeting Info<span class="form-label-description"></span></label>
                            <textarea class="form-control" name="example-textarea-input" rows="6" placeholder="Please write something about this event ..." onInput={(e) => {this.handleChange({ info: e.target.value })}}></textarea>
                          </div>

                          <div class="mb-3">
                            <label class="form-label">Meeting Type</label>
                            <div class="form-selectgroup">
                              <label class="form-selectgroup-item">
                                <input type="checkbox" name="name" value="in_person" class="form-selectgroup-input" checked="" onClick={(e) => this.changeType(e.target.value)} />
                                <span class="form-selectgroup-label">In Person</span>
                              </label>
                              <label class="form-selectgroup-item">
                                <input type="checkbox" name="name" value="hybrid" class="form-selectgroup-input" onClick={(e) => this.changeType(e.target.value)}/>
                                <span class="form-selectgroup-label">Hybrid</span>
                              </label>
                              <label class="form-selectgroup-item">
                                <input type="checkbox" name="name" value="online" class="form-selectgroup-input" onClick={(e) => this.changeType(e.target.value)}/>
                                <span class="form-selectgroup-label">Online</span>
                              </label>
                              <label class="form-selectgroup-item">
                                <input type="checkbox" name="name" value="online" class="form-selectgroup-input" onClick={(e) => this.changeType(e.target.value)}/>
                                <span class="form-selectgroup-label">Online External</span>
                              </label>
                            </div>
                          </div>

                          <div class="mb-3">
                            <label class="form-label">Start Date/Time<span class="form-label-description"></span></label>
                            <DatePicker
                              popperPlacement="top-start"
                              showTimeSelect
                              timeFormat="HH:mm"
                              timeIntervals={15}
                              selected={this.state.start_date}
                              timeCaption="time"
                              dateFormat="MMMM d, yyyy h:mm aa"
                              onChange={(date) => {this.handleChange({ start_date: date })}}
                            />
                          </div>

                          <div class="mb-3">
                            <label class="form-label">End Date/Time<span class="form-label-description"></span></label>
                            <DatePicker
                              popperPlacement="top-start"
                              showTimeSelect
                              selected={this.state.end_date}
                              onChange={(date) => {this.handleChange({ end_date: date })}}
                              timeFormat="HH:mm"
                              timeIntervals={15}
                              timeCaption="time"
                              dateFormat="MMMM d, yyyy h:mm aa"
                            />
                          </div>


                        </form>
                      </div>
                      <div class="card-footer">
                        <div class="d-flex">
                          <a href="/#/meetings" class="btn btn-link">Cancel</a>
                          <a href="#" class="btn btn-primary ms-auto">Next</a>
                        </div>
                      </div>
                      <div class="progress progress-sm card-progress">
                        <div class="progress-bar" style={{ width: "38%" }} role="progressbar" aria-valuenow="38" aria-valuemin="0" aria-valuemax="100">
                          <span class="visually-hidden">38% Complete</span>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div class="col-4">
                    <div class="card">
                      <div class="card-status-top bg-lime"></div>
                      <div class="card-body">
                        <h3 class="card-title">Help</h3>
                        <p></p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
