import React from 'react'
import ReactDOM from 'react-dom';
import $ from 'jquery';
import ModelStore from "../../stores/ModelStore";
import * as MyActions from "../../actions/MyActions";
import { dict } from '../../Dict';
import Header from "../header/header.jsx";


export default class EventCreate extends React.Component {

  constructor(props) {
    super(props);
    this.submit = this.submit.bind(this);
    this.setInstance = this.setInstance.bind(this);
    this.handleChange = this.handleChange.bind(this);

    this.state = {
      token: window.localStorage.getItem('token'),
      title: null,
      info: null,
      event_type: null,
    }

  }



  componentWillMount() {
    ModelStore.on("set_instance", this.setInstance);

  }

  componentWillUnmount() {
    ModelStore.removeListener("set_instance", this.setInstance);
  }

  componentDidMount() {

  }



  handleChange(obj) {
    this.setState(obj);
  }

  setInstance() {
    var klass = ModelStore.getKlass()
    var model = ModelStore.getIntance()
    if (klass === 'Event') {
      this.props.history.push("/events/"+model.id)
    }
  }

  submit() {
    var data = this.state
    MyActions.setInstance('events', data, this.state.token);
  }



  changeType(e){
    var self =  this;
    $('.form-selectgroup-input').each(function(){
      if ($(this).val() !== e){
        $(this).prop("checked", false )
      } else {
        if($(this).checked){
          $(this).prop("checked", false )
        } else {
          $(this).prop("checked", true )
          self.setState({event_type: e})
        }
      }
    })
  }


  render() {

    return (
      <body className="antialiased">
        <div className="wrapper">
          <Header />
          <div className="page-wrapper">
            <div className="container-xl">
              <div className="page-header d-print-none">
                <div className="row align-items-center"></div>
                <div className="col">
                  <div className="page-pretitle">You can create an event in this page</div>
                  <h2 className="page-title">Please provide the following information.</h2>
                </div>
              </div>
            </div>
            <div className="page-body">
              <div className="container-xl">
                <div className="row row-deck row-cards">
                  <div class="col-md-8">
                    <div class="card">
                      <div class="card-header">
                        <h3 class="card-title">Create Event</h3>
                      </div>
                      <div class="card-body">
                        <form>
                          <div class="mb-3">
                            <label class="form-label">Event Title</label>
                            <input type="text" onInput={(e) => {this.handleChange({ title: e.target.value })}}  id='title' class="form-control" name="text-input" placeholder="Event's title" />
                          </div>
                          <div class="mb-3">
                            <label class="form-label">Event Info<span class="form-label-description"></span></label>
                            <textarea  onInput={(e) => {this.handleChange({ info: e.target.value })}} class="form-control" id='content' name="example-textarea-input" rows="6" placeholder="Please write something about this event ..."></textarea>
                          </div>

                          <div class="mb-3">
                            <label class="form-label">Event Type</label>
                            <div class="form-selectgroup">
                              <label class="form-selectgroup-item">
                                <input type="checkbox" name="name" value="in_person" class="form-selectgroup-input" checked="" onClick={(e) => this.changeType(e.target.value)} />
                                <span class="form-selectgroup-label">In Person</span>
                              </label>
                              <label class="form-selectgroup-item">
                                <input type="checkbox" name="name" value="hybrid" class="form-selectgroup-input" onClick={(e) => this.changeType(e.target.value)}/>
                                <span class="form-selectgroup-label">Hybrid</span>
                              </label>
                              <label class="form-selectgroup-item">
                                <input type="checkbox" name="name" value="online" class="form-selectgroup-input" onClick={(e) => this.changeType(e.target.value)}/>
                                <span class="form-selectgroup-label">Online</span>
                              </label>
                            </div>
                          </div>

                        </form>
                      </div>
                      <div class="card-footer">
                            <div class="d-flex">
                              <a href="/#/events" class="btn btn-link">Cancel</a>
                              <button onClick={()=> this.submit()} class="btn btn-primary ms-auto">Next</button>
                            </div>
                          </div>
                      <div class="progress progress-sm card-progress">
                        <div class="progress-bar" style={{ width: "38%" }} role="progressbar" aria-valuenow="38" aria-valuemin="0" aria-valuemax="100">
                          <span class="visually-hidden">38% Complete</span>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div class="col-4">
                    <div class="card">
                      <div class="card-status-top bg-lime"></div>
                      <div class="card-body">
                        <h3 class="card-title">Help</h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aperiam deleniti fugit incidunt, iste, itaque minima
                          neque pariatur perferendis sed suscipit velit vitae voluptatem.</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </body>
    )
  }
}
