import React from 'react'
import ModelStore from "../../stores/ModelStore";
import * as MyActions from "../../actions/MyActions";
import { dict } from '../../Dict';
import Header from "../header/header.jsx";
import queryString from 'query-string'

export default class EventAttendance extends React.Component {

    constructor(props) {
        super(props);
        this.setInstance = this.setInstance.bind(this);
        this.getList = this.getList.bind(this);
        this.handleChange = this.handleChange.bind(this);

        this.state = {
            token: window.localStorage.getItem('token'),
            title: null,
            participants: null,
            users: null,
        }

    }
    componentWillMount() {
        ModelStore.on("got_list", this.getList);
        ModelStore.on("set_instance", this.setInstance);
    }

    componentWillUnmount() {
        ModelStore.removeListener("got_list", this.getList);
        ModelStore.removeListener("set_instance", this.setInstance);
    }

    componentDidMount() {
        const value = queryString.parse(this.props.location.search)
        console.log(value)
        MyActions.getList('profiles', this.state.page, {}, this.state.token);
    }


    getList() {
        var users = ModelStore.getList()
        var klass = ModelStore.getKlass()
        if (users && klass === 'Profile') {
            this.setState({
                users: users,
            });
        }
    }



    handleChange(obj) {

    }

    setInstance() {

    }

    addParticipant(){
        const value = queryString.parse(this.props.location.search);
        //if (value.split("_")[0])
        //var data = { attendable_id: , attendable_type: , user_id:}
        //MyActions.setInstance('tasks', data, this.state.token);
    }

    userItems(users) {
        var result = []
        if (users) {
            users.map((user) => {
                result.push(
                    <div class="list-group-item" style={{border: 'none'}}>
                        <div class="row align-items-center">
                            <div class="col-auto">
                                <svg xmlns="http://www.w3.org/2000/svg" class="icon" width="24" height="24" viewBox="0 0 24 24" stroke-width="2" stroke="currentColor" fill="none" stroke-linecap="round" stroke-linejoin="round"><path stroke="none" d="M0 0h24v24H0z" fill="none" /><line x1="12" y1="5" x2="12" y2="19" /><line x1="5" y1="12" x2="19" y2="12" /></svg>                      </div>
                            <div class="col-auto">
                                <a href="#">
                                    <span class="avatar" >{user.initials}</span>
                                </a>
                            </div>
                            <div class="col text-truncate">
                                <a href="#" class="text-body d-block">{user.name}</a>
                                <small class="d-block text-muted text-truncate mt-n1">{user.bio}</small>
                            </div>
                            <div class="col-auto">
                            </div>
                        </div>
                    </div>
                )
            })
        }
        return result
    }


    render() {

        return (
            <body className="antialiased">
                <div className="wrapper">
                    <Header />
                    <div className="page-wrapper">
                        <div className="container-xl">
                            <div className="page-header d-print-none">
                                <div className="row align-items-center"></div>
                                <div className="col">
                                    <div className="page-pretitle">add participants</div>
                                    <h2 className="page-title">{this.state.title}</h2>
                                </div>
                            </div>
                        </div>
                        <div className="page-body">
                            <div className="container-xl">
                                <div className="row row-deck row-cards">
                                    <div className="col-md-6">
                                        <div class="card mb-3">
                                            <div class="card-header">
                                                <h3 class="card-title">All Users</h3>
                                            </div>
                                            <div class="list-group list-group-flush list-group-hoverable">
                                                <div class="list-group-item">
                                                    <div class="input-icon ">
                                                        <input type="text" class="form-control" placeholder="Search…" />
                                                        <span class="input-icon-addon">

                                                            <svg xmlns="http://www.w3.org/2000/svg" class="icon" width="24" height="24" viewBox="0 0 24 24" stroke-width="2" stroke="currentColor" fill="none" stroke-linecap="round" stroke-linejoin="round"><path stroke="none" d="M0 0h24v24H0z" fill="none"></path><circle cx="10" cy="10" r="7"></circle><line x1="21" y1="21" x2="15" y2="15"></line></svg>
                                                        </span>
                                                    </div>
                                                </div>
                                            </div>
                                            {this.userItems(this.state.users)}
                                        </div>
                                    </div>

                                    <div className="col-md-6">
                                        <div class="card mb-3">
                                            <div class="card-header">
                                                <h3 class="card-title">Participants</h3>
                                            </div>
                                            <div class="list-group list-group-flush list-group-hoverable">
                                                <div class="list-group-item">
                                                    <div class="input-icon mb-3">
                                                        <input type="text" class="form-control" placeholder="Search…" />
                                                        <span class="input-icon-addon">

                                                            <svg xmlns="http://www.w3.org/2000/svg" class="icon" width="24" height="24" viewBox="0 0 24 24" stroke-width="2" stroke="currentColor" fill="none" stroke-linecap="round" stroke-linejoin="round"><path stroke="none" d="M0 0h24v24H0z" fill="none"></path><circle cx="10" cy="10" r="7"></circle><line x1="21" y1="21" x2="15" y2="15"></line></svg>
                                                        </span>
                                                    </div>
                                                </div>
                                                {this.userItems(this.state.participants)}
                                            </div>
                                        </div>
                                    </div>

                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </body>
        )
    }
}
