import React from 'react'
import ModelStore from "../../stores/ModelStore";
import * as MyActions from "../../actions/MyActions";
import { dict } from '../../Dict';
import Header from "../header/header.jsx";
import MeetingCard from "../meetings/card.jsx";

export default class EventShow extends React.Component {

  constructor(props) {
    super(props);
    //this.submit = this.submit.bind(this);
    this.getInstance = this.getInstance.bind(this);
    this.handleChange = this.handleChange.bind(this);

    this.state = {
      token: window.localStorage.getItem('token'),
      title: null,
      id: null,
      event_type: null,
      info: null,
      meetings: [],
    }

  }


  componentWillMount() {
    ModelStore.on("got_instance", this.getInstance);
  }

  componentWillUnmount() {
    ModelStore.removeListener("got_instance", this.getInstance);
  }

  componentDidMount() {
    MyActions.getInstance('events', this.props.match.params.id, this.state.token);
  }



  handleChange(obj) {

  }

  getInstance() {
    var klass = ModelStore.getKlass()
    var model = ModelStore.getIntance()
    if (klass === 'Event') {
      this.setState({
        title: model.title,
        id: model.id,
        event_type: model.event_type,
        info: model.info,
        meetings: model.meetings
      })
    }
  }


  render() {

    return (
      <body className="antialiased">
        <div className="wrapper">
          <Header />
          <div className="page-wrapper">
            <div className="container-xl">
              <div className="page-header d-print-none">
                <div className="row align-items-center"></div>
                <div className="col">
                  <div className="page-pretitle">Overview</div>
                  <h2 className="page-title">Condensed dark layout</h2>
                </div>
              </div>
            </div>
            <div className="page-body">
              <div className="container-xl">
                <div className="row  row-cards">

                  <MeetingCard />

                  <div class="col-4">
                    <div class="card">
                      <div class="card-header">
                        <h3 class="card-title">{this.state.title}</h3>
                      </div>
                      <div class="card-body" style={{ minHeight: '200px' }}>
                        <p>{this.state.info}</p>
                      </div>
                      <div class="card-footer">
                        {this.state.event_type}
                      </div>
                    </div>
                  </div>

                </div>
              </div>
            </div>
          </div>
        </div>
      </body>
    )
  }
}
