import React from 'react'
import ModelStore from "../../stores/ModelStore";
import * as MyActions from "../../actions/MyActions";
import { dict } from '../../Dict';
import Header from "../header/header.jsx";
import Moment from 'react-moment';
import 'moment-timezone';
import { event } from 'jquery';

export default class MeetingShow extends React.Component {

  constructor(props) {
    super(props);
    //this.submit = this.submit.bind(this);
    this.getInstance = this.getInstance.bind(this);
    this.handleChange = this.handleChange.bind(this);

    this.state = {
      token: window.localStorage.getItem('token'),
      title: null,
      start_time: null,
      end_time: null,
      info: null,
      capacity: null,
      event: { title: '' },
    }

  }



  componentWillMount() {
    ModelStore.on("got_instance", this.getInstance);
  }

  componentWillUnmount() {
    ModelStore.removeListener("got_instance", this.getInstance);
  }

  componentDidMount() {
    MyActions.getInstance('meetings', this.props.match.params.id, this.state.token);
  }



  handleChange(obj) {

  }

  getInstance() {
    var klass = ModelStore.getKlass()
    var model = ModelStore.getIntance()
    if (klass === 'Meeting') {
      this.setState({
        title: model.title,
        id: model.id,
        start_time: model.start_time,
        end_time: model.end_time,
        info: model.info,
        capacity: model.capacity,
        event: model.event,
      })
    }
  }


  render() {

    return (
      <body className="antialiased">
        <div className="wrapper">
          <Header />
          <div className="page-wrapper">
            <div className="container-xl">
              <div className="page-header d-print-none">
                <div className="row align-items-center"></div>
                <div className="col">
                  <div className="page-pretitle">Overview</div>
                  <h2 className="page-title">{this.state.title}</h2>
                </div>
              </div>
            </div>
            <div className="page-body">
              <div className="container-xl">
                <div className="row  row-cards">


                  <div class="col-lg-4">
                    <div class="card">
                      <div class="card-body">
                        <div class="d-flex align-items-center mb-3">
                          <div class="me-3">

                            <svg xmlns="http://www.w3.org/2000/svg" class="icon" width="24" height="24" viewBox="0 0 24 24" stroke-width="2" stroke="currentColor" fill="none" stroke-linecap="round" stroke-linejoin="round"><path stroke="none" d="M0 0h24v24H0z" fill="none" /><line x1="3" y1="4" x2="21" y2="4" /><path d="M4 4v10a2 2 0 0 0 2 2h12a2 2 0 0 0 2 -2v-10" /><line x1="12" y1="16" x2="12" y2="20" /><line x1="9" y1="20" x2="15" y2="20" /><path d="M8 12l3 -3l2 2l3 -3" /></svg>
                          </div>
                          <div>
                            <small class="text-muted">A meeting of {this.state.event.title}</small>
                            <h3 class="lh-1">{this.state.title}</h3>
                          </div>
                        </div>

                        <h4>Info:</h4>
                        <div class="text-muted mb-3">
                          {this.state.info}
                        </div>

                        <h4>Capacity:</h4>
                        <div class="text-muted mb-3">
                          {this.state.capacity}
                        </div>

                        <h4>Start time/date:</h4>
                        <div class="text-muted mb-3">
                          <Moment parse="YYYY-MM-DD HH:mm">
                            {this.state.start_time}
                          </Moment>
                        </div>

                        <h4>End time/date:</h4>
                        <div class="text-muted mb-3">
                          <Moment parse="YYYY-MM-DD HH:mm">
                            {this.state.end_time}
                          </Moment>
                        </div>

                      </div>
                      <div class="card-footer">
                        This is not legal advice.
                        <a href="#" target="_blank">Learn more about repository licenses.</a>
                      </div>
                    </div>
                  </div>

                  <div class="col-lg-8">
                    <div class="card">
                      <div class="card-body">
                      </div>
                    </div>
                  </div>

                </div>
              </div>
            </div>
          </div>
        </div>
      </body>
    )
  }
}
