import React from 'react'
import ModelStore from "../../stores/ModelStore";
import * as MyActions from "../../actions/MyActions";
import $ from 'jquery';
import { dict } from '../../Dict';
import Header from "../header/header.jsx";

import queryString from 'query-string'
import Quill from 'quill';

export default class FlyerCreate extends React.Component {

  constructor(props) {
    super(props);
    //this.submit = this.submit.bind(this);
    this.setInstance = this.setInstance.bind(this);
    this.handleChange = this.handleChange.bind(this);

    this.state = {
      token: window.localStorage.getItem('token'),
      title: null,
      content: null,
      advertisable_type: null,
      advertisable_id: null,
    }

  }



  componentWillMount() {
    ModelStore.on("set_instance", this.setInstance);

  }

  componentWillUnmount() {
    ModelStore.removeListener("set_instance", this.setInstance);
  }

  componentDidMount() {
    const value = queryString.parse(this.props.location.search);
    var advertisable_type = '';
    var advertisable_id = 0;
    Object.keys(value).map((key) => {
        var splited = key.split("_")
        if (splited[0]) {
            advertisable_type = splited[0].charAt(0).toUpperCase() + splited[0].slice(1);
            advertisable_id = value[key]
        }
    })
    this.setState({ advertisable_type: advertisable_type, advertisable_id: advertisable_id})

    var toolbarOptions = [
        ['bold', 'italic', 'underline', 'strike'],        // toggled buttons
        [{ 'list': 'ordered' }, { 'list': 'bullet' }],   // superscript/subscript        // outdent/indent
        [{ 'direction': 'rtl' }],                         // text direction
        [{ 'header': [1, 2, false] }],
    ];

    var quillOne = new Quill('#editor-one', {
        theme: 'snow',
        modules: {
            toolbar: toolbarOptions,
        },
    });
  }


  handleChange(obj) {
    this.setState(obj);
  }

  setInstance() {
    var klass = ModelStore.getKlass()
    var model = ModelStore.getIntance()
    if (klass === 'Flyer') {
      this.props.history.push("/flyers/"+model.id)
    }
  }

  submit() {
    var data = this.state
    MyActions.setInstance('flyers', data, this.state.token);
  }


  render() {

    return (
      <body className="antialiased">
        <div className="wrapper">
          <Header />
          <div className="page-wrapper">
            <div className="container-xl">
              <div className="page-header d-print-none">
                <div className="row align-items-center"></div>
                <div className="col">
                  <div className="page-pretitle">You can create a Meeting in this page</div>
                  <h2 className="page-title">Please provide the following information.</h2>
                </div>
              </div>
            </div>
            <div className="page-body">
              <div className="container-xl">
                <div className="row row-deck row-cards">
                  <div class="col-md-8">
                    <div class="card">
                      <div class="card-header">
                        <h3 class="card-title">Create Meeting</h3>
                      </div>
                      <div class="card-body">
                        <form>
                          <div class="mb-3">
                            <label class="form-label">Meeting Title</label>
                            <input type="text" class="form-control" name="text-input" placeholder="Event's title" onInput={(e) => { this.handleChange({ title: e.target.value }) }} />
                          </div>
                          <div class="mb-3">
                            <label class="form-label">Meeting Info<span class="form-label-description"></span></label>
                            <textarea class="form-control" name="example-textarea-input" rows="6" placeholder="Please write something about this event ..." onInput={(e) => { this.handleChange({ info: e.target.value }) }}></textarea>
                          </div>




                        </form>
                      </div>
                      <div class="card-footer">
                        <div class="d-flex">
                          <a href="/#/meetings" class="btn btn-link">Cancel</a>
                          <button onClick={()=> this.submit()} class="btn btn-primary ms-auto">Next</button>
                        </div>
                      </div>
                      <div class="progress progress-sm card-progress">
                        <div class="progress-bar" style={{ width: "38%" }} role="progressbar" aria-valuenow="38" aria-valuemin="0" aria-valuemax="100">
                          <span class="visually-hidden">38% Complete</span>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div class="col-4">
                    <div class="card">
                      <div class="card-status-top bg-lime"></div>
                      <div class="card-body">
                        <h3 class="card-title">Help</h3>
                        <p></p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </body>
    )
  }
}
